import json

program = json.loads('[{"op": "HALT"}]')
state = json.loads('{"__input__": 0}')
max_steps = 32

pc = 0
steps = 0
visited = set()
status = "UNKNOWN"

while steps < max_steps:
    key = (pc, tuple(sorted(state.items())))
    if key in visited:
        status = "LOOP"
        break
    visited.add(key)

    if pc < 0 or pc >= len(program):
        status = "CRASH"
        break

    inst = program[pc]
    op = inst['op']

    if op == 'HALT':
        steps += 1
        status = 'HALTS'
        break
    elif op == 'GOTO':
        pc = int(inst['target'])
    elif op == 'JZ':
        reg = str(inst['reg'])
        target = int(inst['target'])
        if state.get(reg, 0) == 0:
            pc = target
        else:
            pc += 1
    elif op == 'DECJNZ':
        reg = str(inst['reg'])
        target = int(inst['target'])
        if state.get(reg, 0) > 0:
            state[reg] = state.get(reg, 0) - 1
            pc = target
        else:
            pc += 1
    elif op == 'SET':
        reg = str(inst['reg'])
        value = inst.get('value', 0)
        if value == '__input__':
            state[reg] = state.get('__input__', 0)
        else:
            state[reg] = int(value)
        pc += 1
    else:
        status = 'ERROR:' + op
        break

    steps += 1
else:
    status = 'UNKNOWN'

__result__ = {
    'status': status,
    'steps': steps,
    'pc': pc,
    'final_state': state,
}

